import { useContext } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { StatusBar } from 'expo-status-bar';

import LoginScreen from './src/screens/login/LoginScreen';
import SignupScreen from './src/screens/signup/SignupScreen';
import WelcomeScreen from './src/screens/welcome/WelcomeScreen';
import { Colors } from './src/constants/styles';
import AuthContextProvider, { AuthContext } from './src/store/auth-context';
import IconButton from './src/components/ui/IconButton';
import BottomTab from './src/shared/bottom-tabs/BottomTab';
import ShowRoomScreen from './src/screens/all-top-hotel/ShowRoomScreen';
import DetailsScreen from './src/screens/details/DetailsScreen';

const Stack = createNativeStackNavigator();

function AuthStack() {
  return (
    <Stack.Navigator
      screenOptions={{
        headerStyle: { backgroundColor: Colors.primary500 },
        headerTintColor: 'white',
        contentStyle: { backgroundColor: Colors.primary100 },
      }}
    >
      <Stack.Screen options={{ headerShown: false }} name='Welcome' component={WelcomeScreen} />
      <Stack.Screen options={{ headerShown: false }} name='Home' component={BottomTab} />
      <Stack.Screen options={{ headerShown: false }} name='DetailsScreen' component={DetailsScreen} />
      <Stack.Screen name='Top Hotels' component={ShowRoomScreen} />
      <Stack.Screen name="Login" component={LoginScreen} />
      <Stack.Screen name="Signup" component={SignupScreen} />
    </Stack.Navigator>
  );
}

function AuthenticatedStack() {
  return (
    <Stack.Navigator screenOptions={{ headerShown: true }} initialRouteName='Welcome'>
      <Stack.Screen options={{ headerShown: false }} name='Home' component={BottomTab} />
      <Stack.Screen name='Top Hotels' component={ShowRoomScreen} />
      <Stack.Screen options={{ headerShown: false }} name='DetailsScreen' component={DetailsScreen} />
    </Stack.Navigator>
  );
}

function Navigation() {
  const authCtx = useContext(AuthContext)
  return (
    <NavigationContainer>
      <Stack.Navigator options={{ headerShown: false }} initialRouteName={authCtx.isAuthenticated ? "LoggedIn" : "NotLoggedIn"}>
        <Stack.Screen options={{ headerShown: false }} name='LoggedIn' component={AuthenticatedStack}></Stack.Screen>
        <Stack.Screen options={{ headerShown: false }} name='NotLoggedIn' component={AuthStack}></Stack.Screen>
      </Stack.Navigator>
    </NavigationContainer>

  );
}

export default function App() {
  return (
    <>
      <StatusBar style="light" />
      <AuthContextProvider>
        <Navigation />
      </AuthContextProvider>

    </>
  );
}
